import time


def ti(func):
    def wrapper(*args, **kwargs):
        t1 = time.time()
        ret = func(*args, **kwargs)
        t2 = time.time()
        print("循环时间是：{:.3}".format(t2 - t1))
        return ret

    return wrapper

def exgcd(a, b):
    if b == 0:
        x, y = 1, 0
        return a, x, y
    r, x, y = exgcd(b, a % b)
    x, y = y, x - (a // b) * y
    return r, x, y


